const targetSessionId = $('Interacing').first().json.sessionId;
const interacingData = $('Interacing').first().json; // Store once for reuse
const inputItems = $input.all();

// Find the matching item
const matchedItem = inputItems.find(item => {
  return item.json.activeHumanRequestChatId === targetSessionId || 
         item.json.activeHumanChannel === targetSessionId;
});

// Return single output object with all required properties
return [{
  json: {
    status: matchedItem ? "active" : "notFound",
    chatInput: interacingData.chatInput,
    channel: interacingData.channel,
    sessionId: interacingData.sessionId
  }
}];

const inputItems = $input.all();
const targetId = $('Interacing').first().json.sessionId;

// Find the matching item (100% guaranteed to exist)
const matchedItem = inputItems.find(item => 
    item.json.userChatId === targetId || 
    item.json.humanChatId === targetId
);

// Determine which field matched and get the paired value
const isUserChatMatch = matchedItem.json.userChatId === targetId;
const pairedValue = isUserChatMatch ? matchedItem.json.humanChatId : matchedItem.json.userChatId;

// Return only the required fields
return [{
    json: {
        sessionId: targetId,
        userChatId: isUserChatMatch ? targetId : pairedValue,
        humanChatId: isUserChatMatch ? pairedValue: targetId,
        channel: isUserChatMatch ? $input.first().json.channel:$input.first().json.channel
    }
}];
